<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Agent extends CI_Controller {        	public function index(){            $this->dashboard();        }                public function dashboard(){         $system = new SYSTEM();         $data = $this->get_active_agents();           $system->container($data,'PRINT_R!');        }                private function get_active_agents(){           $this->load->model('model_agent');           $active = $this->model_agent->get_active_agents();           return $active;        }}